function videoStop(){$('video')[0].pause();$('#btn-video').text('재생')}
function videoPlay(){$('.active video')[0].play();$('#btn-video').text('멈춤')}
function audioPlay(){$('.active video').prop('muted',!1);$('#btn-audio').text('끄기')}
function audioStop(){$('video').prop('muted',!0);$('#btn-audio').text('켜기')}
function fnVideoSP(el){if($(el).text()=='멈춤'){videoStop()}else{videoPlay()}}