$('.main-nav').click(function(){$(this).next('.sub-nav').stop().slideToggle(300);$(this).toggleClass('on').siblings().removeClass('on');$(this).next('.sub-nav').siblings('.sub-nav').slideUp(300)});$(window).scroll(function(){if($(this).scrollTop()>50){$('.h-bottom').hide()}else{$('.h-bottom').show()}});$('#btn-video').click(function(){fnVideoSP(this)})
$('#btn-audio').click(function(){if($(this).text()=='켜기'){audioPlay()}else{audioStop()}})
const secThreeEventSwiper=new Swiper(".event-swiper",{grabCursor:!0,effect:"creative",pagination:{el:".swiper-pagination",type:"fraction",},creativeEffect:{prev:{translate:[0,0,-200],},next:{translate:["100%",0,0],},},autoplay:{delay:1200,}});$(window).scroll(function(){if($(this).scrollTop()>1000){$('.btn-up').fadeIn()}else{$('.btn-up').fadeOut()}})
$('.btn-up').click(function(){event.preventDefault();$('html, body').animate({scrollTop:0},300)})